  `descripcion_ing` text COLLATE utf8_spanish_ci NOT NULL,
  `dia` varchar(2) COLLATE utf8_spanish_ci NOT NULL,
  `mes` varchar(200) COLLATE utf8_spanish_ci NOT NULL,
  `ano` year(4) NOT NULL,
